import React, { useState } from 'react';
import axios from 'axios';
import './Newmod.css';

const Newmod = ({ show, onClose, onApprove, userRole }) => {
  const [comments, setComments] = useState('');

  const submitComments = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/comments', { comments });
      console.log('Comments submitted successfully:', response.data);
    } catch (error) {
      console.error('Error submitting comments:', error);
    }
  };

  const handleApprove = () => {
    submitComments();
    onApprove(comments);
    setComments('');
  };

  if (!show) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Comments</h2>
        <div className="comments-section">
          <label htmlFor="comments">Comments:</label>
          <textarea
            id="comments"
            value={comments}
            onChange={(e) => setComments(e.target.value)}
            rows="4" required
          ></textarea>
        </div>
        {userRole !== 'employee' && (
          <div className="modal-actions">
            <button className="approve" onClick={handleApprove}>Approve</button>
          </div>
        )}
        <button className="close" onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default Newmod;
