// Navbar.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useUser } from '../UserContext/UserContext'; // Import the context
import './Navbar.css';
import logout from '../../assets/logout.png';

const Navbar = () => {
  const { user, setUser } = useUser(); // Access the user from the context
  const navigate = useNavigate();

  const handleLogout = () => {
    setUser(null); // Clear the user from the context
    navigate('/login');
  };

  return (
    <div className="navbar">
      <div className="navbar-logo"> greenko</div>
      <div className="navbar-title">Document Management</div>
      <div className="navbar-user">
        <div className="user-name">{user}</div>
        <div className="logout" onClick={handleLogout}><img src={logout} alt="" /></div>
      </div>
    </div>
  );
};

export default Navbar;
