import './App.css';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import React, { useState } from 'react';
import LoginSignup from './Pages/LoginSignup';
import Admin from './Components/admin/admin';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
          {/* Route for LoginSignup component */}
          {!isLoggedIn && <Route path="/" element={<LoginSignup onLogin={() => setIsLoggedIn(true)} />} />}

          {/* Route for authenticated content (Main page) */}
          {isLoggedIn && <Route path="/" element={<Admin />} />}

          {/* Redirect to LoginSignup if not logged in */}
          {!isLoggedIn && <Route path="*" element={<Navigate to="/" replace />} />}
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
