import React, { useState } from 'react';
import Modal from '../Modal/Modal';
import './Policies.css';

const Policies = () => {
  const [showModal, setShowModal] = useState(false);
  const [policies, setPolicies] = useState([]);
  const [formData, setFormData] = useState({
    locationType: '',
    sbu: '',
    department: ''
  });

  const toggleModal = () => {
    setShowModal(!showModal);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setPolicies([...policies, formData]);
    setFormData({ locationType: '', sbu: '', department: '' });
    setShowModal(false);
  };

  return (
    <div className='policies'>
      <div className='list'>
        <div><h3>List of Policies</h3></div>
        <div className='icbut' onClick={toggleModal}><h3> + Create</h3></div>
      </div>
      <Modal show={showModal} onClose={toggleModal}>
        <div className="form-container">
          <h2>Create New Policy</h2>
          <form onSubmit={handleSubmit}>
            <div>
              <label>Location Type:</label>
              <input
                type="text"
                name="locationType"
                value={formData.locationType}
                onChange={handleInputChange}
                required
              />
            </div>
            <div>
              <label>SBU:</label>
              <textarea
                name="sbu"
                value={formData.sbu}
                onChange={handleInputChange}
                required
              ></textarea>
            </div>
            <div>
              <label>Department/Function Name:</label>
              <textarea
                name="department"
                value={formData.department}
                onChange={handleInputChange}
                required
              ></textarea>
            </div>
            <div>
              <button type="submit">Create</button>
            </div>
          </form>
        </div>
      </Modal>
      <div className="policies-list">
        {policies.map((policy, index) => (
          <div key={index} className="policy-item">
            <h4>Policy {index + 1}</h4>
            <p><strong>Location Type:</strong> {policy.locationType}</p>
            <p><strong>SBU:</strong> {policy.sbu}</p>
            <p><strong>Department/Function Name:</strong> {policy.department}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Policies;
