import './App.css';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import React, { useState } from 'react';
import LoginSignup from './Pages/LoginSignup';
import Navbar from './Components/Navbar/Navbar';
import { UserProvider } from './Components/UserContext/UserContext'; // Import the UserProvider context
import MainContent from './Components/MainContent/MainContent'
import Policies from './Components/Policies/Policies';
import Drafts from './Components/Drafts/Drafts'

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  return (
    <div className="App">
      <BrowserRouter>
        <UserProvider> {/* Wrap only the necessary components with UserProvider */}
          <Navbar />
          <Routes>
            <Route path="/login" element={<LoginSignup onLogin={handleLogin} />} />
            <Route path="/" element={isLoggedIn ? <MainContent /> : <Navigate to="/login" replace />} />
            <Route path='/admin/policies' element={isLoggedIn ? <Policies /> : <Navigate to="/login" replace />} />
            <Route path='/edit' element={isLoggedIn ? <Drafts /> : <Navigate to="/login" replace />} />
          </Routes>
        </UserProvider>
      </BrowserRouter>
    </div>
  );
}

export default App;
