import './App.css';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import React, { useState } from 'react';
import LoginSignup from './Pages/LoginSignup';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Function to handle successful login (you can pass this function to LoginSignup component)
  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
          {/* Route for LoginSignup component */}
          <Route path="/" element={<LoginSignup onLogin={handleLogin} />} />

          {/* Redirect to login if not logged in */}
          {!isLoggedIn && <Route path="*" element={<Navigate to="/" replace />} />}
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
