import './App.css';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import React, { useState, useNavigate } from 'react';
import LoginSignup from './Pages/LoginSignup';
import Navbar from './Components/Navbar/Navbar';
import Sidebar from './Components/Sidebar/Sidebar';
import MainContent from './Components/MainContent/MainContent';
import Policies from './Components/Policies/Policies';
import Drafts from './Components/Drafts/Drafts'


function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate(); // Import the useNavigate hook

  const handleLogin = () => {
    setIsLoggedIn(true);
    navigate('/'); // Navigate to the root route after successful login
  };

  return (
    <div className="App">
      <BrowserRouter>
        {isLoggedIn && <Navbar />} {/* Navbar is only shown when user is logged in */}
        <div className="main-container">
          {isLoggedIn && <Sidebar />} {/* Sidebar is only shown when user is logged in */}
          <div className="content">
            <Routes>
              <Route path="/login" element={<LoginSignup onLogin={handleLogin} />} />
              {/* Redirect to login page if not logged in */}
              {!isLoggedIn && <Route path="*" element={<Navigate to="/login" replace />} />}
              {/* Show MainContent only when logged in */}
              {isLoggedIn && <Route path="/" element={<MainContent/>}/>}
              <Route path='/admin/policies' element={<Policies/>}/>
              <Route path='/edit' element={<Drafts/>}/>
            </Routes>
          </div>
        </div>
      </BrowserRouter>
    </div>
  );
}

export default App;
