import React, { useState } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import LoginSignup from './Pages/LoginSignup';
import Navbar from './Components/Navbar/Navbar';
import Sidebar from './Components/Sidebar/Sidebar';
import MainContent from './Components/MainContent/MainContent';
import Policies from './Components/Policies/Policies';
import Drafts from './Components/Drafts/Drafts';
import ViewPolicy from './Components/ViewPolicy/ViewPolicy';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userName, setUserName] = useState('');
  const [userRole, setUserRole] = useState('');

  const handleLogin = (name, role) => {
    setIsLoggedIn(true);
    setUserName(name);
    setUserRole(role);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserName('');
    setUserRole('');
  };

  return (
    <div className="App">
      <BrowserRouter>
        <Navbar userName={userName} userRole={userRole} onLogout={handleLogout} />
        {isLoggedIn ? (
          <div className="main-container">
            <Sidebar />
            <div className="content">
              <Routes>
                <Route path="/" element={<MainContent />} />
                <Route path="/admin/policies" element={<Policies userRole={userRole} />} />
                <Route path="/edit" element={<Drafts />} />
                <Route path="/view" element={<ViewPolicy />} />
                <Route path="*" element={<Navigate to="/" />} />
              </Routes>
            </div>
          </div>
        ) : (
          <Routes>
            <Route path="/login" element={<LoginSignup onLogin={handleLogin} setUserRole={setUserRole} />} />
            <Route path="*" element={<Navigate to="/login" />} />
          </Routes>
        )}
      </BrowserRouter>
    </div>
  );
}

export default App;
