import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Import useHistory from react-router-dom

const Login = ({ onLogin }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [loginError, setLoginError] = useState('');
  const history = useHistory(); // Access history object to navigate programmatically

  const changeHandler = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:4001/login', formData);

      if (response.status === 200) {
        onLogin(response.data.user); // Pass user data to parent component
        // Redirect to '/' route upon successful login
        history.push('/');
      } else {
        setLoginError(response.data.message);
      }
    } catch (error) {
      console.error('Login Error:', error);
      setLoginError('An error occurred during login.');
    }
  };

  return (
    <div className="auth-form">
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input type="email" name="email" value={formData.email} onChange={changeHandler} required />
        </label>
        <label>
          Password:
          <input type="password" name="password" value={formData.password} onChange={changeHandler} required />
        </label>
        {loginError && <p className="error-message">{loginError}</p>}
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
