import React, { useState } from 'react';
import Modal from '../Modal/Modal';
import './Policies.css';
import './Form.css'; // Import the new CSS file

const Policies = () => {
  const [showModal, setShowModal] = useState(false);

  const toggleModal = () => {
    setShowModal(!showModal);
  };

  return (
    <div className='policies'>
      <div className='list'>
        <div><h3>List of Policies</h3></div>
        <div className='icbut' onClick={toggleModal}><h3> + Create</h3></div>
      </div>
      <Modal show={showModal} onClose={toggleModal}>
        <div className="form-container">
          <h2>Create New Policy</h2>
          <form>
            <div>
              <label>Location Type:</label>
              <input type="text" name="policyName" required />
            </div>
            <div>
              <label>SBU:</label>
              <textarea name="policyDescription" required></textarea>
            </div>
            <div>
              <label>Department/Function Name:</label>
              <textarea name="policyDescription" required></textarea>
            </div>
            <div>
              <button type="submit">Create</button>
            </div>
          </form>
        </div>
      </Modal>
    </div>
  );
}

export default Policies;
