import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { PoliciesContext } from '../Components/PoliciesContext/PoliciesContext';

const LoginSignup = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ name: '', password: '', role: 'employee' });
  const { setUser } = useContext(PoliciesContext);
  const navigate = useNavigate();

  const toggleForm = () => {
    setIsLogin(!isLogin);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevFormData => ({ ...prevFormData, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        const response = await axios.post('http://localhost:4001/login', {
          name: formData.name,
          password: formData.password
        });
        setUser({ name: response.data.name, role: response.data.role });
        localStorage.setItem('token', response.data.token);
        navigate('/policies');
      } else {
        const response = await axios.post('http://localhost:4001/signup', {
          name: formData.name,
          password: formData.password,
          role: formData.role
        });
        setUser({ name: response.data.name, role: response.data.role });
        localStorage.setItem('token', response.data.token);
        navigate('/policies');
      }
    } catch (error) {
      console.error(`${isLogin ? 'Login' : 'Signup'} failed:`, error);
      alert(`${isLogin ? 'Login' : 'Signup'} failed`);
    }
  };

  return (
    <div className="auth-container">
      <h2>{isLogin ? 'Login' : 'Signup'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" name="name" value={formData.name} onChange={handleInputChange} required />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" name="password" value={formData.password} onChange={handleInputChange} required />
        </div>
        {!isLogin && (
          <div>
            <label>Role:</label>
            <select name="role" value={formData.role} onChange={handleInputChange} required>
              <option value="employee">Employee</option>
              <option value="manager">Manager</option>
              <option value="head">Head</option>
            </select>
          </div>
        )}
        <button type="submit">{isLogin ? 'Login' : 'Signup'}</button>
      </form>
      <button onClick={toggleForm}>
        {isLogin ? 'Switch to Signup' : 'Switch to Login'}
      </button>
    </div>
  );
};

export default LoginSignup;
