import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Sidebar.css';

const Sidebar = () => {
  const [adminOpen, setAdminOpen] = useState(false);
  const [siteOpen, setSiteOpen] = useState(false);
  const [policiesOpen, setPoliciesOpen] = useState(false);

  const toggleAdmin = () => setAdminOpen(!adminOpen);
  const toggleSite = () => setSiteOpen(!siteOpen);
  const togglePolicies = () => setPoliciesOpen(!policiesOpen);

  return (
    <div className="sidebar">
      <Link to="/dashboard" className="sidebar-item">Dashboard</Link>

      <div className="sidebar-section" onClick={togglePolicies}>Policies</div>
      {policiesOpen && (
        <div className="sidebar-subitems">
          <Link to="/policies/drafts" className="sidebar-item">Drafts</Link>
          <Link to="/policies/pendings" className="sidebar-item">Pendings</Link>
          <Link to="/policies/in-review" className="sidebar-item">In Review</Link>
          <Link to="/policies/published" className="sidebar-item">Published</Link>
        </div>
      )}

      <Link to="/procedures" className="sidebar-item">Procedures</Link>
      <Link to="/formats" className="sidebar-item">Formats</Link>

      <div className="sidebar-section" onClick={toggleAdmin}>Admin</div>
      {adminOpen && (
        <div className="sidebar-subitems">
          <Link to="/admin/policies" className="sidebar-item">Policies</Link>
          <Link to="/admin/procedures" className="sidebar-item">Procedures</Link>
        </div>
      )}

      <div className="sidebar-section" onClick={toggleSite}>Site</div>
      {siteOpen && (
        <div className="sidebar-subitems">
          <Link to="/site/policies" className="sidebar-item">Policies</Link>
          <Link to="/site/procedures" className="sidebar-item">Procedures</Link>
        </div>
      )}
    </div>
  );
};

export default Sidebar;
