import React, { useState } from 'react';
import { useEffect } from 'react'
import './LoginSignup.css'; 
import axios from 'axios';
const LoginSignup = () => {

  const [name,setName] = useState()
  const [email,setEmail] = useState()
  const [password, setPassword] = useState()

  const handleSubmit = (e)=>{
    e.preventDefault()
    axios.post('http://localhost:3001/login',{name,email,password})
    .then(result=>console.log(result))
    .catch(err=>console.log(err))
  }


  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  const [isLogin, setIsLogin] = useState(true);

  const toggleForm = () => {
    setIsLogin((prev) => !prev);
  };

  return (
    <div className={`auth-container ${isLogin ? 'login' : 'signup'}`}>
      <div className="auth-form-container">
        <div className="auth-info">
          <h2>{isLogin ? 'Welcome Back!' : 'Create an Account'}</h2>
          <p>{isLogin ? 'Login to your account' : 'Sign up to get started'}</p>
        </div>
        <div className="auth-form">
          <form onSubmit={handleSubmit}>

            <label>
              Name:
              <input type="name" name="name" onChange={(e)=>setName(e.target.value)} required/>   
            </label>
            <label>
              Email:
              <input type="email" name="email" onChange={(e)=>setEmail(e.target.value)} required />
            </label>
            <label>
              Password:
              <input type="password" name="password" onChange={(e)=>setPassword(e.target.value)} required />
            </label>
            {!isLogin && (
              <label>
                Confirm Password:
                <input type="password" name="confirmPassword" required />
              </label>
            )}
            <button type="submit">{isLogin ? 'Login' : 'Sign Up'}</button>
          </form>
          <p onClick={toggleForm} className="toggle-form">
            {isLogin ? 'Don\'t have an account? Sign Up' : 'Already have an account? Login'}
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginSignup;
